import { SRLWrapper } from "simple-react-lightbox";
import sanityClient from '../client.js';
// USE THE IMPORT BELOW INSTEAD IF YOU ARE USING THE PRO VERSION
// import { SRLWrapper } from 'simple-react-lightbox-pro'

const [postData, setPost] = useState(null)
useEffect(() => {
    sanityClient
    .fetch(`*[_type == "post" && title match "Digital"]{
        title,
        slug,
        mainImage {
            asset -> {
            _id,
            url,
            },
            alt
        }
    }`)
    .then((data) => setPost(data))
    .catch(console.error);
}, [])

function Lightbox() {
  return (

     <SRLWrapper options={options}>
                {<div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8" id="gallery-with-links" className="content" >

                    {postData && postData.map((post, index) => ( 
                    <article type="button" onClick={showModal}>

                       {/*  <Link to={'/post/' + post.slug.current} key={post.slug.current} > */}
                        <span
                         className="block h-64 relative rounded shadow leading-snug bg-white col-image-with-link" key={index}
                        >
                            <img src={post.mainImage.asset.url}
                             alt={post.mainImage.alt}
                             className="w-full h-full rounded-r object-cover absolute"
                            />
                            <span className="block relative h-full flex justify-end items-end pr-4 pb-4">
                                <h3 className="text-gray-800 text-lg font-blog px-3 py-4 bg-red-700 text-red-100">
                                    {post.title}
                                </h3>
                            </span>
                        </span>
                        {/* </Link> */}
                    </article>
                    ))}

                </div>}
                </SRLWrapper>

  );
}

export default Lightbox;
