import React from 'react'
import {useState, useEffect} from 'react'
import {useParams} from 'react-router-dom'
import imageUrlBuilder from '@sanity/image-url'
import BlockContent from '@sanity/block-content-to-react'
import sanityClient from '../client'

const builder = imageUrlBuilder(sanityClient);
function  urlFor(source){
    return builder.image(source)
}

export default function SinglePost({showModal}) {

    const [singlePost, setSinglePost] = useState(null)
    const {slug} = useParams()

    useEffect(() => {
        sanityClient.fetch(`*[slug.current == "${slug}"]{
            title,
            _id,
            slug,
            mainImage{
                asset->{
                    _id,
                    url
                }
            },
            body,
            "name": author->name,
            "authorImage": author->image
        }`)
        .then((data) => setSinglePost(data[0]))
        .catch(console.error)

    },[slug]);

    if (!singlePost) return <div>Loading...</div>

    return(
        <main className="bg-gray-200 min-h-screen p-12" type="button" onPress={showModal} >
            <article className="containter shadow-lg mx-auto bg-green-100 rounded-lg">
                <header className="relative">
                    <div className="absolute h-full w-full flex items-center justifu-center ">
                    <div className="bg-white bg-opacity-75 rounded p-12">
                        <h1 className="cursive text-3xl lg:text-6xl mb-4">
                           {singlePost.title}
                        </h1>
                        <div><img src={urlFor(singlePost.authorImage).url()}
                         alt={singlePost.name}
                         className="w-10 h-10 rounded-full"
                        />
                        <p className="cursive flex items-center pl-2 text-2xl">{singlePost.name}</p>
                        </div>
                    </div>
                    </div>
                    <img src={singlePost.mainImage.asset.url} alt={singlePost.title} className="w-full object-cover rounded-t" style={{height:'400px'}} />
                </header>
                <div className="px-16 lg:px-48 py-12 lg:py-20 prose lg:prose-xl max-w-full">
                   <BlockContent blocks={singlePost.body} projectId='db9v6xd6' dataset='production'></BlockContent>
                </div>
            </article>
        </main>
    )
}
