import React from 'react'
import sanityClient from '../client.js';
import { useEffect, useState } from 'react';
import {Link} from 'react-router-dom'

export default function Post() {
const [postData, setPost] = useState(null)
useEffect(() => {
    sanityClient
    .fetch(`*[_type == "post"]{
        title,
        slug,
        mainImage {
            asset -> {
            _id,
            url,
            },
            alt
        }
    }`)
    .then((data) => setPost(data))
    .catch(console.error);
}, [])

const getDigital = setPost.filter = () => (`*[_type == "post" && _title == "Digital"]{
    title,
    slug,
    mainImage {
        asset -> {
        _id,
        url,
        },
        alt
    }
}`)

    return (
        <main className='background-green-100 min-h-screen p-12'>
            <section className="container mx-auto" >
                <h1 className="text-5xl flex justify-center cursive">Portfolio Page</h1>
                <h2 className="text-lg text-gray-600 flex justify-center mb-12">Welcome to my portfolio page</h2>
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {postData && postData.map((post, index) => ( 
                    <article>
                        <button onClick={getDigital}>button</button>
                        <Link to={'/post/' + post.slug.current} key={post.slug.current} >
                        <span
                         className="block h-64 relative rounded shadow leading-snug bg-white" key={index}
                        >
                            <img src={post.mainImage.asset.url}
                             alt={post.mainImage.alt}
                             className="w-full h-full rounded-r object-cover absolute"
                            />
                            <span className="block relative h-full flex justify-end items-end pr-4 pb-4">
                                <h3 className="text-gray-800 text-lg font-blog px-3 py-4 bg-red-700 text-red-100">
                                    {post.title}
                                </h3>
                            </span>
                        </span>
                        </Link>
                    </article>
                    ))}
                </div>
            </section>
        </main>
    )
}
